import { useState } from 'react';

export const ShippingForm = () => {
  const [isPickup, setIsPickup] = useState(false);

  const handleCheckboxChange = (e) => {
    setIsPickup(e.target.checked);
  };

  return (
    <>
      <h1>Shipping Page</h1>
      <p>Please select your shipping method:</p>
      <div>
        <input
          type="checkbox"
          id="pickup"
          checked={isPickup}
          onChange={handleCheckboxChange}
        />
        <label htmlFor="pickup">Pickup</label>
      </div>
      <div>
        <input
          type="checkbox"
          id="home-delivery"
          checked={!isPickup}
          onChange={handleCheckboxChange}
        />
        <label htmlFor="home-delivery">Home Delivery</label>
      </div>
      {isPickup ? (
        <div>
          <p>You have chosen pickup.</p>
          <p>Please come to our store to pick up your order.</p>
          <p>Our address is: 123 Main St, Anytown, USA</p>
        </div>
      ) : (
        <div>
          <p>You have chosen home delivery.</p>
          <p>Your order will be delivered to the address you provided.</p>
          <div>
            <label htmlFor="address">Delivery Address:</label>
            <input type="text" id="address" />
          </div>
          <div>
            <label htmlFor="city">City:</label>
            <input type="text" id="city" />
          </div>
          <div>
            <label htmlFor="state">State:</label>
            <input type="text" id="state" />
          </div>
          <div>
            <label htmlFor="zip">Zip Code:</label>
            <input type="text" id="zip" />
          </div>
        </div>
      )}
    </>
  );
}
