import { useState } from "react";
import "./shipping.css";

export const ShippingForm = () => {
  const [isPickup, setIsPickup] = useState(true);

  const handleCheckboxChange = (e) => {
    setIsPickup(e.target.checked);
  };

  return (
    <div className="shipping-container">
      <div className="shipping-options">
        <label htmlFor="pickup-option" className="shipping-label" defaultChecked>
          <input
            type="checkbox"
            id="pickup-option"
            className="shipping-checkbox"
            checked={isPickup}
            onChange={handleCheckboxChange}
          />
          Pickup
        </label>
        <label htmlFor="delivery-option" className="shipping-label">
          <input
            type="checkbox"
            id="delivery-option"
            className="shipping-checkbox"
            checked={!isPickup}
            onChange={(e) => setIsPickup(!e.target.checked)}
          />
          Standard Delivery
        </label>
      </div>
      {isPickup ? (
        <div className="pickup-info">
          <p>Thank you for choosing pickup!</p>
          <p>Please pick up your order at our store:</p>
          <address>123 Main St, Anytown USA</address>
        </div>
      ) : (
        <div className="delivery-info">
          <p>Thank you for choosing standard delivery!</p>
          <p>Your order will be delivered to the address you provided:</p>
          <address>123 Main St, Anytown USA</address>
        </div>
      )}
    </div>
  );
}
