import { useState } from "react";
import "./shipping.css";

export const ShippingForm = () => {
    const [deliveryMethod, setDeliveryMethod] = useState('delivery');
    const [showPickupList, setShowPickupList] = useState(false);

    const handleChange = (event) => {
      if (event.target.checked) {
        setDeliveryMethod('pickup');
        setShowPickupList(true);
      } else {
        setDeliveryMethod('delivery');
        setShowPickupList(false);
      }
    };

    return (
      <div className="shipping-container">
        <h2>Shipping Method</h2>
        <div>
          <label htmlFor="delivery" className={deliveryMethod === 'delivery' ? 'selected' : ''}>Standard Delivery</label>
          <input type="radio" id="delivery" name="delivery-method" value="delivery" checked={deliveryMethod === 'delivery'} onChange={() => setDeliveryMethod('delivery')} />
        </div>
        <div>
          <label htmlFor="pickup" className={deliveryMethod === 'pickup' ? 'selected' : ''}>Pickup</label>
          <input type="checkbox" id="pickup" name="delivery-method" value="pickup" checked={deliveryMethod === 'pickup'} onChange={handleChange} />
        </div>
        {showPickupList && (
          <div>
            <h3>Pickup Locations</h3>
            <ul>
              <li>Location 1</li>
              <li>Location 2</li>
              <li>Location 3</li>
            </ul>
          </div>
        )}
      </div>
    );
}
