import React, { useState } from "react";
import "./checkout.css"

export const Checkout = () => {
	const [pickUpOption, setPickUpOption] = useState(false);

	const handlePickUpOptionChange = (event) => {
		setPickUpOption(event.target.checked);
	};

	return (
		<div>
			<h1>Checkout</h1>
			<form >
				<label>
					<input
						type="checkbox"
						checked={pickUpOption}
						onChange={handlePickUpOptionChange}
					/>
					Pick up at a nearby location
				</label>
				{pickUpOption && (
					<div>
						<h3>Pick-up Points</h3>
						{/* List of pick-up points goes here */}
					</div>
				)}
				{<label>
					<input
						type="checkbox"
						checked={pickUpOption}
						onChange={handlePickUpOptionChange}
					/>
					Standard Delivery
				</label>}
			</form>
		</div>
	);
};
